mapper.select:
SELECT 

FROM tb_contacts a

WHERE a.id = #{id}

SELECT 

FROM tb_contacts a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.f_name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.f_gender = #{gender}


		AND a.f_department = #{department}


		AND a.f_title = #{title}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_contacts a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_contacts SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
