mapper.select:
SELECT 

FROM tb_customer a

WHERE a.id = #{id}

SELECT
FROM tb_customer a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.f_deptid LIKE '%'||#{deptId}||'%' '%'+#{deptId}+'%' concat('%',#{deptId},'%')
AND a.f_name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.f_statusid = #{statusId}
AND a.f_industoryid = #{industoryId}
AND a.f_cuslevel = #{cusLevel}
AND a.f_legalperson LIKE '%'||#{legalPerson}||'%' '%'+#{legalPerson}+'%' concat('%',#{legalPerson},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
